SELECT CASE a.dbtype WHEN 1 THEN 'MYSQL' WHEN 2 THEN 'ORACLE' WHEN 3 THEN 'MS SQL SERVER'END dbTypeName, a.id id, a.org_id orgId, o.org_name orgName, a.name name, a.dbtype dbType, a.db_name dbName, a.host host, a.port port, a.username username, a.password password, a.remark remark, a.table_space tableSpaces, a.index_space indexSpaces, a.creator creator, a.createtime createtime, a.updater updater, a.updatetime updatetime FROM data_etl_dataconnection a, sys_org o where a.org_id=o.id
and a.dbtype = #{dbType}
and (a.name like CONCAT('%', #{nameOrDBName}, '%') OR a.db_name like CONCAT('%', #{nameOrDBName}, '%') )
and a.org_id in (
#{item}
)
order by a.updatetime desc

