mapper.select:
SELECT c.code_id codeId, c.node_id nodeId, c.code, c.name, c.description, n.name nodeName FROM info_codestandard_code c inner join info_codestandard_node n on c.node_id= n.node_id where 1=1
and c.sourceid = #{sourceId}
and c.node_id in ( select t3.node_id from info_codestandard_node t2 inner join info_codestandard_node t3 on t2.node_id = t3.parentnode_id where t2.node_id= #{nodeId} )
and c.node_id = #{nodeId}
and (c.code like CONCAT('%', #{keyWord}, '%') or c.name like CONCAT('%', #{keyWord}, '%') )
order by c.node_id

SELECT t1.node_id nodeId, t1.parentnode_id parentnodeId, t1. CODE CODE, t1.`name` NAME, t1.description description, t1.node_type nodeType FROM info_codestandard_node t1 WHERE t1.sourceid = 1 AND t1.parentnode_id IN ( SELECT t2.node_id nodeId FROM info_codestandard_node t2 WHERE t2.sourceid = 1 AND t2.parentnode_id =#{parentId} )
union all
SELECT t1.node_id nodeId, t1.parentnode_id parentnodeId, t1. CODE CODE, t1.`name` NAME, t1.description description, t1.node_type nodeType FROM info_codestandard_node t1 WHERE t1.sourceid = 1 and t1.parentnode_id=#{parentId}
union all SELECT t1.node_id nodeId, t1.parentnode_id parentnodeId, t1. CODE CODE, t1.`name` NAME, t1.description description, t1.node_type nodeType FROM info_codestandard_node t1 WHERE t1.sourceid = 1 and t1.node_id=#{parentId}
