/* START( "start" ), END( "end" ), STOP( "stop" ), ERROR( "error" ), RUNNING( "running" ), PAUSED( "paused" );*/ SELECT log.id_batch id, m.task_name transname, (case log.STATUS WHEN 'start' THEN '开始' WHEN 'end' THEN '结束' WHEN 'stop' THEN '停止' WHEN 'running' THEN '运行中' WHEN 'paused' THEN '暂停' END )status, log.LINES_INPUT line_input, (select count(1) from kettle_error_log ll where ll.channel_id=log.channel_id ) line_rejected, log.startdate startdate, log.enddate enddate, log.logdate logdate, log.log_field log_field, log.channel_id channel_id, o.org_name orgName, o.id orgId FROM kettle_log log, data_etl_task m, sys_org o WHERE log.transname = m.task_id AND m.org_id = o.id and m.business_type = #{businessType}
AND m.task_name LIKE CONCAT('%',#{transName},'%')
and log.LOGDATE >= concat(#{startTime} , ' 00:00:00') and log.LOGDATE <= concat(#{endTime} , ' 23:59:59')
and m.org_id in (
#{item}
) ORDER BY log.logdate DESC
