mapper.select:
SELECT a.code_name subsetName, a.sourceid sourceid, b.subset_code subsetCode, b.class_code classCode, b.class_name className, b.description description, b.creator creator, b.createtime createtime, b.updater updater, b.updatetime updatetime
FROM info_metadata_subset a,info_metadata_class b, info_metadata_source c where a.code=b.subset_code AND a.sourceid = c.sourceid AND c.sourceid = #{sourceId}
and b.subset_code = #{subsetCode}
and (b.class_code like CONCAT('%', #{classCodeOrclassName}, '%') OR b.class_name like CONCAT('%', #{classCodeOrclassName}, '%') )
order by b.updatetime desc
